head.script:
scwin.onpageload = function () {

};

/**
 * @event
 * @name btn_normal_onclick
 * @description 정상 버튼을 클릭한다.
 */
scwin.btn_normal_onclick = function (e) {

     const options ={
        id : "sbm_normal",
        action : "/ui/data/normal.json", 
        method : "get",
        processMsg : "조회중입니다",
    };

    $c.nova.trxDataAsync(options);
};

/**
 * @event
 * @name btn_exception_onclick
 * @description Java Exception 버튼 클릭, Java Business 오류 메시지를 출력한다.
 */
scwin.btn_exception_onclick = function (e) {
    const options= {
        id : "sbm_exception",
        action : "/ui/data/exception.json", 
        method : "get",
        processMsg : "조회중입니다",
    };

    $c.nova.trxDataAsync(options);    
};

/**
 * @event
 * @name btn_404_onclick
 * @description HTTP StatusCode가 300이상 200미만인 경우 StatusCode와 URI, responseBody를 상세창에 출력
 */
scwin.btn_404_onclick = function (e) {
    const options = {
        id : "sbm_404",
        action : "/ui/data/404.json", 
        method : "get",
        processMsg : "조회중입니다",
    };

    $c.nova.trxDataAsync(options);    
};

/**
 * @event
 * @name btn_logOut_onclick
 * @description Log out 버튼 클릭, 로그인 Session 정보가 만료된 경우 Alert창을 띄운다.
 */
scwin.btn_logOut_onclick = function (e) {

    const options= {
        id : "sbm_logOut",
        action : "/ui/data/logout.json", 
        method : "get",
        processMsg : "조회중입니다",
    };
    $c.nova.trxDataAsync(options);   
};

scwin.btn_success_onclick = function (e) {
    $c.win.showToastMessage("S", "Success");
};

scwin.btn_error_onclick = function (e) {
    $c.win.showToastMessage("E", "Error");
};

scwin.btn_warning_onclick = function (e) {
    $c.win.showToastMessage("W", "Warning");
};

scwin.btn_info_onclick = function (e) {
    $c.win.showToastMessage("I", "Info");
};
