/**
 * @property
 * @name style
 * @category 01.Basic & ETC
 * @type string
 * @option
 * @default
 * @necessary N
 * @bindparent
 * @description desc
 */
scwin.onpageload = function() {
    // scwin.setUI();
};
scwin.setUI = function () {
    row_link_gen.removeAll();
    const links = dlt_linkView.getAllJSON();
    links.forEach((link, idx) => {
        row_link_gen.insertChild(idx);
        const genChildren = row_link_gen.getChild(idx, "link");
        genChildren.setHref(link.linkUrl);
        genChildren.setValue(link.linkUrl)
    });
}


/**
 * @method
 * @name setReloadData
 * @description 외부에서 데이터를 메소드로 수정 시 실행
 * @param {array} linkArr Link의 배열 데이터
 * ex) [
 *         {
 *             linkUrl: 'https://naver.com'
 *         },
 *         { 
 *             linkUrl: 'https://google.com'
 *         }
 *     ]
 * @returns
 * @hidden N
 * @exception
 * @example
 */
scwin.setReloadData = function (linkArr) {
    dlt_linkView.setData(linkArr);
    scwin.setUI();
};

/**
 * @method
 * @name getData
 * @description Link 데이터 반환 (Array)
 * @param
 * @returns
 * @hidden N
 * @exception
 * @example
 */
scwin.getData = function () {
    const links = dlt_linkView.getAllJSON();
    if (!links.length) return [];
    return links;
};

/**
 * @method
 * @name setLoadData
 * @description 데이터가 있는 페이지(수정,상세화면)에서 로드 시 실행
 * @param {array} linkArr Link의 배열 데이터
 * ex) [
 *         {
 *             linkUrl: 'https://naver.com'
 *         },
 *         { 
 *             linkUrl: 'https://google.com'
 *         }
 *     ]
 * @returns
 * @hidden N
 * @exception
 * @example
 */
scwin.setLoadData = async function (linkArr) {
    await dlt_linkView.setJSON(linkArr);
    scwin.setUI();
};
